// Filter.js
import React, { useState } from 'react';

const Filter = ({ onFilterChange, movies, setFilteredMovies }) => {
  const [filter, setFilter] = useState('');
  const [filterBy, setFilterBy] = useState('names'); // Default filter option

  const handleFilterChange = ({ filterBy, value }) => {
    setFilterBy(filterBy);
    setFilter(value);
  };

  const handleFilterSubmit = () => {
    if (filter.trim() === '') {
        return;
      }
    // Filter the movies based on the current filter criteria
    const filtered = movies.filter(movie => {
      switch (filterBy) {
        case 'names':
          return movie.name && movie.name.toLowerCase().includes(filter.toLowerCase());
        case 'director':
          return movie.director && movie.director.toLowerCase().includes(filter.toLowerCase());
        case 'releaseYear':
          return movie.releaseYear && movie.releaseYear.toString().includes(filter);
        case 'language':
          return movie.language && movie.language.toLowerCase().includes(filter.toLowerCase());
        default:
          return true; // No filter
      }
    });
  // Update the state with the filtered movies
  setFilteredMovies(filtered);

  // Trigger the parent component callback
  onFilterChange({ filterBy, value: filter });
  };

  const handleFilterByChange = (e) => {
    const value = e.target.value;
    setFilterBy(value);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleFilterSubmit();
    }
  };

  return (
    <div className="mb-3">
      <label htmlFor="filter" className="form-label">
        Filter Movies:
      </label>
      <div className="input-group mb-3">
        <select
          className="form-select"
          onChange={handleFilterByChange}
          value={filterBy}
        >
          <option value="names">Name</option>
          <option value="director">Director</option>
          <option value="releaseYear">Release Year</option>
          <option value="language">Language</option>
        </select>
        <input
          type="text"
          className="form-control"
          id="filter"
          value={filter}
          onChange={(e) => handleFilterChange({ filterBy, value: e.target.value })}
          onKeyDown={handleKeyDown}
          placeholder={`Enter ${filterBy} to filter`}
        />
        <button className="btn btn-primary" onClick={handleFilterSubmit}>
          Filter
        </button>
      </div>
    </div>
  );
};

export default Filter;
