import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UpdateMovieForm = ({ isUpdateForm, location }) => {
  const [movieData, setMovieData] = useState({
    movieName: '',
    language: '',
    director: '',
    releaseYear: '',
    rating: '',
  });

  const navigate = useNavigate();

  useEffect(() => {
    if (isUpdateForm && location.state && location.state.movieData) {
      // If in update mode and movie data is provided, pre-fill the form
      setMovieData(location.state.movieData);
    }
  }, [isUpdateForm, location.state]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMovieData({ ...movieData, [name]: value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    try {
      const updatedMovieData = {
        ...movieData,
        releaseYear: parseInt(movieData.releaseYear),
        rating: parseInt(movieData.rating),
      };

      if (isUpdateForm) {
        // Update existing movie
        const response = await axios.put(
          `http://localhost:5000/update?movieName=${encodeURIComponent(
            movieData.movieName
          )}`,
          updatedMovieData
        );
        console.log('Update response:', response.data);
      } else {
        // Add new movie
        const response = await axios.post(
          'http://localhost:5000/addMovies',
          updatedMovieData
        );
        console.log('Add response:', response.data);
      }

      // Redirect to the movie list page
      navigate('/');

      // Reload the page after a delay (adjust the delay as needed)
      setTimeout(() => {
        window.location.reload();
      }, 500);
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">
        {isUpdateForm ? 'Update Movie' : 'Add New Movie'}
      </h2>
      <form onSubmit={handleFormSubmit}>
        {/* ... (previous code) */}
        <button type="submit" className="btn btn-primary">
          {isUpdateForm ? 'Update Movie' : 'Add Movie'}
        </button>
      </form>
    </div>
  );
};

export default UpdateMovieForm;
