// App.js

import React, { useState, useEffect } from 'react';
import MovieList from './MovieList';
import { Modal, Button } from 'react-bootstrap'; // Import modal components


const App = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedMovie, setSelectedMovie] = useState(null); // Track the selected movie for the modal
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetch('http://localhost:5000/')  // Update the endpoint
      .then((response) => response.json())
      .then((data) => {
        setMovies(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching movies:', error);
        setLoading(false);
      });
  }, []);

  const handleMovieClick = (movie) => {
    setSelectedMovie(movie);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setSelectedMovie(null);
    setShowModal(false);
  };

  return (
    <div>
      <h1 className="text-center mt-3">Movie List</h1>
      {loading ? ( <p className="text-center">Loading...</p>) : (
        <>
        <MovieList movies={movies} />
      <Modal show={showModal} onHide={handleCloseModal}>
      <Modal.Header closeButton>
        <Modal.Title>{selectedMovie.movieName}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <p>{selectedMovie.description}</p>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleCloseModal}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
        </>
      ) }
    </div>
  );
};

export default App;
