import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Modal } from 'react-bootstrap';


const MovieList = ({ movies }) => {
  const [movieData, setMovieData] = useState([]);
  const [movieDataAll, setMovieDataAll] =useState([]);
 
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
//   const handleShow = () => ;


  const fetchMovieDataAll = async (movieName) =>{
    try{
        const response = await fetch(
            'http://localhost:5000/search/${movieName}'
        );
        if(!response.ok){
            console.log("Error");
        }

        const dataAll = response.json();
        setMovieDataAll(dataAll);
        setShow(true);
    }
    catch{
        console.log("error !!!");
    }
}


  useEffect(() => {
    const fetchMovieData = async () => {
      const apiKey = '5aefe5fe';
      const baseUrl = 'http://www.omdbapi.com/';

      const moviePosters = movies.map(async (movie) => {
        const response = await fetch(
          `${baseUrl}?apikey=${apiKey}&t=${encodeURIComponent(movie)}&type=movie`
        );

        if (!response.ok) {
          console.error(`Error fetching data for ${movie}`);
          return null;
        }

        const data = await response.json();

        if (data && data.Title && data.Type === 'movie') {
          return {
            name: movie,
            poster: data.Poster,
          };
        } else {
          return{
            name:movie,
            poster:null,
          };
        }
      });

      const movieResults = await Promise.all(moviePosters);
      setMovieData(movieResults.filter(movie => movie !== null));
    };

    fetchMovieData();
  }, [movies]);

  return (
    <div className="container mt-5" >
      <div className="row" onClick={fetchMovieDataAll}>
        {movieData
          .sort((a, b) => a.name.localeCompare(b.name)) // Sort movies alphabetically by name
          .map((movie, index) => (
            <div key={index} className="col-md-4 mb-4">
              <div className="card">
                {movie.poster ? (
                  <img
                    src={movie.poster}
                    className="card-img-top"
                    alt={`${movie.name} Poster`}
                    style={{ height: '500px' }} 
                  />
                ) : (
                  <div className="no-poster">
                    <p>No Image Found</p>
                  </div>
                )}
                <div className="card-body">
                  <h5 className="card-title">
                    {movie.name}
                  </h5>
                </div>
              </div>
            </div>
          ))}
      </div>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{movieDataAll.movieName}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <p>{movieDataAll.director}</p>
            <p>{movieDataAll.language}</p>
            <p>{movieDataAll.releaseYear}</p>
            <p>{movieDataAll.rating}</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default MovieList;
