import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const MovieList = ({ movies }) => {
  const [movieData, setMovieData] = useState([]);

  useEffect(() => {
    const fetchMovieData = async () => {
      const apiKey = '5aefe5fe';
      const baseUrl = 'http://www.omdbapi.com/';

      const moviePromises = movies.map(async (movie) => {
        const response = await fetch(
          `${baseUrl}?apikey=${apiKey}&t=${encodeURIComponent(movie)}&type=movie`
        );

        if (!response.ok) {
          console.error(`Error fetching data for ${movie}`);
          return null;
        }

        const data = await response.json();

        if (data && data.Title && data.Type === 'movie' && data.Language === 'Tamil') {
          return {
            name: movie,
            poster: data.Poster,
          };
        } else {
          console.error(`Missing data for ${movie}`);
          return null;
        }
      });

      const movieResults = await Promise.all(moviePromises);
      setMovieData(movieResults.filter(movie => movie !== null));
    };

    fetchMovieData();
  }, [movies]);

  return (
    <div className="container mt-5">
      <div className="row">
        {movieData.map((movie, index) => (
          <div key={index} className="col-md-4 mb-4">
            <div className="card">
              {movie.poster ? (
                <img
                  src={movie.poster}
                  className="card-img-top"
                  alt={`${movie.name} Poster`}
                />
              ) : (
                <div className="no-poster">
                  <p>No Image Found</p>
                </div>
              )}
              <div className="card-body">
                <h5 className="card-title">
                  {movie.name}
                </h5>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MovieList;
