import React, { Component } from "react";
import axios from 'axios';

export default class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movieName: '',
      res: []
    }

    this.handleSearch = this.handleSearch.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.fetchPoster=this.fetchPoster.bind(this);
  }

  handleChange(event) {
    this.setState({ movieName: event.target.value });
  }

  handleSearch(event) {
    event.preventDefault();
    axios.post('http://localhost:5000/search', { movieName: this.state.movieName })
      .then(response => {
        this.setState({ res: response.data });
      })
      .catch(error => console.log(error))
  }

  render() {
    return (
      <div className="container mt-4">
        <form onSubmit={this.handleSearch} className="mb-3">
          <div className="input-group">
            <input
              type="text"
              name="movieName"
              onChange={this.handleChange}
              className="form-control"
              placeholder="Enter movie name"
            />
            <div className="input-group-append">
              <button type="submit" className="btn btn-primary">
                Search
              </button>
            </div>
          </div>
        </form>

        {Array.isArray(this.state.res) && this.state.res.map(movie => (
          <div key={movie.id} className="card mt-3">
            <div className="card-body">
              <h5 className="card-title">{movie.movieName}</h5>
              <p className="card-text">Director: {movie.director}</p>
              <p className="card-text">Release Year: {movie.releaseYear}</p>
              <p className="card-text">Language: {movie.language}</p>
              <p className="card-text">Rating: {movie.rating}</p>
            </div>
          </div>
        ))}
      </div>
    );
  }
}
