// src/components/LandingPage.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import MovieDetails from './MovieDetails';
import MovieDetails from './movieDetails';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);

  useEffect(() => {
    // Fetch movies from the backend when the component mounts
    axios.get('http://localhost:5000')
      .then(response => setMovies(response.data))
      .catch(error => console.error(error));
  }, []);

  const handleSearch = (query) => {
    setSearchQuery(query);
    // Use the correct endpoint for searching movies in your backend
    axios.get(`http://localhost:5000/search`,movieName={query})
      .then(response => {
        setSelectedMovie(response.data);
      })
      .catch(error => console.error(error));
  };

  const handleMovieClick = (movie) => {
    // Set the selected movie when a movie card is clicked
    setSelectedMovie(movie);
  };

  return (
    <div>
      <h1>Movies</h1>

      {/* Search bar at the top */}
      <div>
        <input type="text" placeholder="Search by name" value={searchQuery} onChange={(e) => handleSearch(e.target.value)} />
      </div>

      {/* Movie grid in the center */}
      <div className="movie-grid">
        {movies.map(movie => (
          <div key={movie} className="movie-card" onClick={() => handleMovieClick(movie)}>
            <h3>{movie}</h3>
          </div>
        ))}
      </div>

      {/* Display movie details */}
      <MovieDetails movie={selectedMovie} />
    </div>
  );
};

export default App;
