import React, { Component } from "react";
import axios from 'axios';

export default class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movieName: '', // Corrected property name
      res: '' // Added res property
    }

    this.handleSearch = this.handleSearch.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({ movieName: event.target.value });
  }

  handleSearch(event) {
    event.preventDefault();
  
    axios.post('http://localhost:5000/search', { movieName: this.state.movieName })
      .then(response => {
        console.log(response.data);
        this.setState({ res: response.data })
      })
      .catch(error => console.log(error))
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSearch}>
        <input type='text' name='movieName' onChange={this.handleChange} />
        <button type="submit">Search</button>
        </form>
        <div>{this.state.movieName}</div>
        <div>
        {Array.isArray(this.state.res) ? (
          this.state.res.map(movie => <div key={movie.id}>{movie.title}</div>)
        ) : (
          <p>No movies found</p>
        )}
      </div>      </div>
    );
  }
}
