import React, { useState, useEffect } from 'react';
import MovieList from './MovieList';
import Search from './searchMovie'
import Filter from './Filter';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredMovies, setFilteredMovies] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/')  // Update the endpoint
      .then((response) => response.json())
      .then((data) => {
        setMovies(data);
        setFilteredMovies(data)
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching movies:', error);
        setLoading(false);
      });

  }, []);

  const handleFilterChange = (filter) => {
    const filtered = movies.filter((movie) =>
      movie.toLowerCase().includes(filter.toLowerCase())
    );
    setFilteredMovies(filtered);
  };

  return (
    <div>
      <h1 className="text-center mt-3">Movie List</h1>
      <Search/>
      <Filter onFilterChange={handleFilterChange} />
      {loading ? (<p className="text-center">Loading...</p>) : (<MovieList movies={movies}  />)}
    </div>
  );
};

export default App;
