import React, { useState, useEffect } from 'react';
import MovieList from './MovieList';
import Search from './searchMovie'
import Filter from './Filter';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredMovies, setFilteredMovies] = useState([]);

  useEffect(() => {
    endpoint='http://localhost:5000/'
    fetchMovies(endpoint);
  }, []);

  const fetchMovies = async (endpoint) => {
    try {
      const response = await fetch(endpoint);
      const data = await response.json();
      setMovies(data);
      setFilteredMovies(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching movies:', error);
      setLoading(false);
    }
  };

  const handleFilterChange = ({ filterBy, value }) => {
    // Modify the URL based on the selected filterBy
    const endpoint = `http://localhost:5000/filter?${filterBy}=${encodeURIComponent(
      value
    )}`;
    // Fetch movies with the updated endpoint
    fetchMovies(endpoint);
  };

  return (
    <div>
      <h1 className="text-center mt-3">Movie List</h1>
      <Search/>
      <Filter onFilterChange={handleFilterChange} />
      {loading ? (<p className="text-center">Loading...</p>) : (<MovieList movies={filteredMovies}  />)}
    </div>
  );
};

export default App;
