import React, { useState, useEffect } from 'react';
import MovieList from './MovieList';
import Search from './searchMovie';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import UpdateMovieForm from './UpdateMovieForm';

const App = () => {
    <Router>
      <Route path="/" exact component={MovieList} />
      <Route path="/update/:movieName" component={UpdateMovieForm} />
  </Router>
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5000/')  // Update the endpoint
      .then((response) => response.json())
      .then((data) => {
        setMovies(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching movies:', error);
        setLoading(false);
      });
  }, []);


  return (
    <div>

      <h1 className="text-center mt-3">Movie List</h1>
      <Search/>
      {loading ? (<p className="text-center">Loading...</p>) : (<MovieList movies={movies}  />)}
    </div>
  );
};

export default App;
