import React, { useState } from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';

const MovieForm = ({ onClose, onMovieAdded }) => {
  const [movieData, setMovieData] = useState({
    movieName: '',
    language: '',
    director: '',
    relaseYear: '',
    rating: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMovieData({ ...movieData, [name]: value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    try {
      const updatedMovieData = {
        ...movieData,
        relaseYear: parseInt(movieData.relaseYear),
        rating: parseInt(movieData.rating),
      };

      const response = await axios.post('http://localhost:5000/addMovies', updatedMovieData);
      console.log(response.data);

      // Notify the parent component that a movie has been added
      onMovieAdded();

      // Close the form
      onClose();

      // Reload the page
      window.location.reload();
    } catch (error) {
      console.error('Error adding movie:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center">Add New Movie</h2>
      <form onSubmit={handleFormSubmit}>
        <div className="mb-3">
          <label htmlFor="movieName" className="form-label">
            Name:
          </label>
          <input
            type="text"
            className="form-control"
            id="movieName"
            name="movieName"
            value={movieData.movieName}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="language" className="form-label">
            Language:
          </label>
          <input
            type="text"
            className="form-control"
            id="language"
            name="language"
            value={movieData.language}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="director" className="form-label">
            Director:
          </label>
          <input
            type="text"
            className="form-control"
            id="director"
            name="director"
            value={movieData.director}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="relaseYear" className="form-label">
            Release Year:
          </label>
          <input
            type="number"
            className="form-control"
            id="relaseYear"
            name="relaseYear"
            value={movieData.relaseYear}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="rating" className="form-label">
            Rating:
          </label>
          <input
            type="number"
            className="form-control"
            id="rating"
            name="rating"
            value={movieData.rating}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Add Movie
        </button>
      </form>
    </div>
  );
};

MovieForm.propTypes = {
  onClose: PropTypes.func.isRequired,
  onMovieAdded: PropTypes.func.isRequired,
};

export default MovieForm;
