import React, { useState, useEffect } from 'react';
import MovieList from './MovieList';
import Search from './searchMovie'
import Filter from './Filter';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredMovies, setFilteredMovies] = useState(movies);

  useEffect(() => {
    const endpoint='http://localhost:5000/'
    fetchMovies(endpoint);
  }, []);

  const fetchMovies = async (endpoint) => {
    try {
      console.log('Fetching movies with endpoint:', endpoint);
      const response = await fetch(endpoint);
      const data = await response.json();
      console.log('Received movies data:', data);
      setMovies(data);
      setFilteredMovies(data);
      console.log("eeeeeeeeeeeeeeee")
      console.log(filteredMovies);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching movies:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    console.log("Filtered Movies updated:", filteredMovies);
}, [filteredMovies]);



  const handleFilterChange = async ({ filterBy, value }) => {
    try {
      // Construct the endpoint based on the selected filterBy and value
      const endpoint = `http://localhost:5000/filterName?${filterBy}=${encodeURIComponent(value)}`;

      // Fetch movies with the updated endpoint
      const response = await fetch(endpoint);
      const filteredData = await response.json();

      // Update the filteredMovies state with the filtered data
      setFilteredMovies(filteredData);
    } catch (error) {
      console.error('Error fetching filtered movies:', error);
    }
  };


  return (
    <div>
      <h1 className="text-center mt-3">Movie List</h1>
      <Search/>
      <Filter onFilterChange={handleFilterChange} movies={movies} setFilteredMovies={setFilteredMovies} />
      {loading ? (<p className="text-center">Loading...</p>) : (<MovieList movies={filteredMovies}  />)}
    </div>
  );
};

export default App;
