// App.js

import React, { useState, useEffect } from 'react';
import MovieList from './MovieList';
import SearchBar from './components/SearchBar';


const App = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (value) => {
    setSearchTerm(value);
    // Perform search or any other actions based on the search term
  };

  useEffect(() => {
    fetch('http://localhost:5000/')  // Update the endpoint
      .then((response) => response.json())
      .then((data) => {
        setMovies(data);
        // setFilteredMovies(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching movies:', error);
        setLoading(false);
      });
  }, []);

  return (
    <div>
      <h1 className="text-center mt-3">Movie List</h1>
      <SearchBar onSearch={handleSearch} />
      {loading ? ( <p className="text-center">Loading...</p>) : (<MovieList movies={movies} />) }
    </div>
  );
};

export default App;
