import React, { useState, useEffect } from 'react';
import MovieList from './MovieListFile';
import Search from './searchMovie';
import Filter from './Filter';
import { BrowserRouter as Router, Route, Routes, Link} from 'react-router-dom';
import MovieForm from './AddMovieForm';
import UpdateMovieForm from './UpdateMovie';
import './styles.css';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredMovies, setFilteredMovies] = useState([]);

  useEffect(() => {
    const endpoint = 'http://localhost:5000/';
    fetchMovies(endpoint);
  }, []);

  const fetchMovies = async (endpoint) => {
    try {
      const response = await fetch(endpoint);
      const data = await response.json();
      setMovies(data);
      setFilteredMovies(data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching movies:', error);
      setLoading(false);
    }
  };

  const handleFilterChange = async ({ filterBy, value }) => {
    try {
      const endpoint = `http://localhost:5000/filterName?${encodeURIComponent(filterBy)}=${encodeURIComponent(value)}`;
      const response = await fetch(endpoint);
      const filteredData = await response.json();
      setFilteredMovies(filteredData);
    } catch (error) {
      console.error('Error fetching filtered movies:', error);
    }
  };

  const headingStyle = {
    fontFamily: 'Georgia, serif', // Using Roboto font
    fontSize: '2rem',
    fontWeight: 'bold',
    color: 'Maroon',
  };


  return (
    <Router>
      <div className="app-container">
        <h1 className="text-center mt-3" style={headingStyle}>Movie List Application</h1>
        <div className="text-center mb-3">
          <Link to="/add-movie" className="btn btn-primary">
            Add Movie
          </Link>
          {'   '} 
          <Link to="/update-movie" className="btn btn-primary">
            Update Movie
          </Link>
        </div>
        <Routes>
          <Route path="/add-movie" element={<MovieForm />} />

          <Route path='/update-movie' element={<UpdateMovieForm />}/>

          <Route
            path="/"
            element={(
              <>
                <Search />
                <Filter onFilterChange={handleFilterChange} movies={movies} setFilteredMovies={setFilteredMovies} />
                {loading ? (
                  <p className="text-center">Loading...</p>
                ) : (
                  <MovieList movies={filteredMovies}/>
                )}
              </>
            )}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
