// App.js

import React, { useState, useEffect } from 'react';
import MovieList from './MovieList';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredMovies, setFilteredMovies] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/')  // Update the endpoint
      .then((response) => response.json())
      .then((data) => {
        setMovies(data);
        setFilteredMovies(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching movies:', error);
        setLoading(false);
      });
  }, []);

  const handleSearch= async (searchQuery)=>{

    try{
      const response=await fetch(`http://localhost:5000/search/${encodeURIComponent(searchQuery)}`)

      if(!response.ok){
        console.log("Error !!!");
        return;
      }

      const movieDetails = await response.json();
      setFilteredMovies([movieDetails]);

    }
    catch(error){
      console.log(error);
    }
  }

  return (
    <div>
      <h1 className="text-center mt-3">Movie List</h1>
      {loading ? (
        <p className="text-center">Loading...</p>
      ) : (
        <MovieList movies={movies} />
      )}
    </div>
  );
};

export default App;
