import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Modal } from 'react-bootstrap';
import axios from 'axios';
import deleteMovie from './DeleteMovies';

class MovieList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movieData: [],
      res :[],
      show: false,
    };
  }

  handleShow = () => this.setState({ show: true });
  handleClose = () => this.setState({ show: false });

  handleDeleteClick = (movieName) => {
    // Add logic for deleting the movie using axios or any other method
    // For example, you can make an API call to delete the movie
    // and then update the state accordingly
    console.log(`Deleting movie: ${movieName}`);
    // Perform the delete operation and update the state
  };

  componentDidMount() {
    this.fetchMovieData();
  }

  async fetchMovieData() {
    const { movies } = this.props;
    const apiKey = '5aefe5fe';
    const baseUrl = 'http://www.omdbapi.com/';

    const moviePosters = await Promise.all(
      movies.map(async (movie) => {
        try {
          const response = await axios.get(
            `${baseUrl}?apikey=${apiKey}&t=${encodeURIComponent(movie)}&type=movie`
          );

          const responseAll = await axios.post(
            'http://localhost:5000/search',{movieName: movie}
          );

          this.setState({res: responseAll});
          console.log(responseAll);
          const data = response.data;

          if (data && data.Title && data.Type === 'movie') {
            return {
              name: movie,
              poster: data.Poster,
            };
          } else {
            return {
              name: movie,
              poster: null,
            };
          }
        } catch (error) {
          console.error(`Error fetching data for ${movie}`);
          return null;
        }
      })
    );

    const movieResults = moviePosters.filter((movie) => movie !== null);
    this.setState({ movieData: movieResults });
  }

  render() {
    const { movieData, show } = this.state;

    return (
      <div className="container mt-5">
        <div className="row">
          {movieData
            .sort((a, b) => a.name.localeCompare(b.name))
            .map((movie, index) => (
              <div key={index} className="col-md-4 mb-4" onClick={this.handleShow}>
                <div className="card">
                  {movie.poster ? (
                    <img
                      src={movie.poster}
                      className="card-img-top"
                      alt={`${movie.name} Poster`}
                      style={{ height: '500px' }}
                    />
                  ) : (
                    <div className="no-poster">
                      <p>No Image Found</p>
                    </div>
                  )}
                  <div className="card-body">
                    <h5 className="card-title">{movie.name}</h5>
                  </div>
                </div>
              </div>
            ))}
        </div>

        <Modal show={show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you are reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={() => this.handleDeleteClick('ExampleMovie')}>
              Delete
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}

export default MovieList;
