import logo from './logo.svg';
import './App.css';
import { useState,useEffect } from 'react';
import axios from'axios';

function App() {

  const[movies,setMovies]=useState([]);
  const[search,setQuery]=useState([]);
  const[selectedMovie, setSelectedMovie]=useState(null);

  useEffect(()=>{
    axios.get('http://localhost:5000/')
    .then(response=> setMovies(response.data))
    .catch(error => console.error(error));
  },[]);

  const handleSearch=(query)=>{
    setQuery(query);
    axios.get('http://localhost:5000/search',{movieName : query})
    .then(respone=>{
      setSelectedMovie(respone.data);
    })
    .catch(error=>console.error(error));
  }

  return (
    <div className="App">
      <h1>Movies</h1>

      <div>
        <input type='text' placeholder='search by name' onChange={(e)=>handleSearch(e.target.value)}/>
      </div>

      <div className='movie-grid'>
        {movies.map(movie=>(
          <div key={movie._id} className="movie-card">
            <h3>{movie.name}</h3>
            <p>{movie.director}</p>
            <p>{movie.language}</p>
            <p>{movie.releaseYear}</p>

            <button onClick={() => handleDelete(movie._id)}>Delete</button>
            <button onClick={() => handleUpdate(movie._id, /* pass updated data */)}>Update</button>
          </div>
        ))}
      </div>
      <MovieDetails movie={selectedMovie} />
    </div>
  );
}

export default App;
