import React, { useState, useEffect } from 'react';
import MovieList from './MovieList';
import { Button, Modal } from 'react-bootstrap';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  //pop up
  const [showModal, setShowModal] = useState(false);

  // Function to open the modal
  const handleShow = () => setShowModal(true);

  // Function to close the modal
  const handleClose = () => setShowModal(false);

  useEffect(() => {
    fetch('http://localhost:5000/')  // Update the endpoint
      .then((response) => response.json())
      .then((data) => {
        setMovies(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching movies:', error);
        setLoading(false);
      });
  }, []);



  return (
    <div>
      <h1 className="text-center mt-3">Movie List</h1>
      {loading ? (<p className="text-center">Loading...</p>) : (<MovieList movies={movies} onClick={handleShow} />)}

      <Modal show={showModal} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Hello</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>Hello, this is your pop-up message!</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

    </div>
  );
};

export default App;
