import React, { Component } from "react";
import axios from 'axios';

export default class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      word: '' // Corrected property name
    }

    this.handleSearch = this.handleSearch.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value }); // Corrected property name
  }

  handleSearch(event) {
    event.preventDefault();
    console.log(this.state)
    axios.get('http://localhost:5000/search', this.state)
      .then(response => {
        console.log(response.data);
        document.getElementById("op").innerHTML = response.data;
        this.setState({ res: response.data })
      })
      .catch(error => console.log(error))
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSearch}>
          <input type='text' name='word' onChange={this.handleChange} />
          <button type="submit">Search</button>
        </form>
        <div id='op'></div>
      </div>
    );
  }
}
