// App.js

import React, { useState, useEffect } from 'react';
import MovieList from './MovieList';
import SearchBar from './SearchBar';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredMovies, setFilteredMovies] = useState([]);
  const [selectedMovieDetails, setSelectedMovieDetails] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/')  // Update the endpoint
      .then((response) => response.json())
      .then((data) => {
        setMovies(data);
        setFilteredMovies(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching movies:', error);
        setLoading(false);
      });
  }, []);

  const handleSearch = async (searchQuery) => {
    try {
      const response = await fetch(`http://localhost:5000/search/${encodeURIComponent(searchQuery)}`);

      if (!response.ok) {
        console.log("Error !!!");
        return;
      }

      const movieDetails = await response.json();
      setSelectedMovieDetails(movieDetails);
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <div>
      <h1 className="text-center mt-3">Movie List</h1>
      <SearchBar onSearch={handleSearch} />
      {loading ? (
        <p className="text-center">Loading...</p>
      ) : (
        <div>
          {selectedMovieDetails ? (
            <div>
              <h2 className="text-center">{selectedMovieDetails.name}</h2>
              <p>Language: {selectedMovieDetails.language}</p>
              <p>Director: {selectedMovieDetails.director}</p>
              <p>Release Year: {selectedMovieDetails.releaseYear}</p>
              <p>Rating: {selectedMovieDetails.rating}</p>
            </div>
          ) : (
            <MovieList movies={filteredMovies} />
          )}
        </div>
      )}
    </div>
  );
};

export default App;
