import React, { Component } from "react";
import axios from 'axios';

export default class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movieName: '', // Corrected property name
      res: [] // Added res property
    }

    this.handleSearch = this.handleSearch.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    this.setState({ movieName: event.target.value });
  }

handleSearch(event) {
    event.preventDefault();
    console.log("hello", this.state);
  
    axios.post('http://localhost:5000/search', { movieName: this.state.movieName }) // Include data as the second argument
      .then(response => {
        console.log(response.data);

        const resultString = JSON.stringify(response.data);

        document.getElementById("op").innerHTML = resultString
        this.setState({ res: response.data });
      })
      .catch(error => console.log(error))
  }
  

  render() {
    return (
      <div className="container mt-4">
        <form onSubmit={this.handleSearch} className="mb-3">
          <div className="input-group">
            <input
              type="text"
              name="movieName"
              onChange={this.handleChange}
              className="form-control"
              placeholder="Enter movie name"
            />
            <div className="input-group-append">
              <button type="submit" className="btn btn-primary">
                Search
              </button>
            </div>
          </div>
        </form>
        <div className="mb-3">{this.state.movieName}</div>
        <div id="op">
        {Array.isArray(this.state.res) && this.state.res.map(movie => (
            <div key={movie.id}>
                <p>Name: {movie.movieName}</p>
                <p>Director: {movie.director}</p>
                <p>ReleaseYear: {movie.relaseYear}</p>
                <p>Language: {movie.language}</p>
            </div>
        ))}
        </div>

      </div>
    );
  }
  
}
