import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Modal } from 'react-bootstrap';
import axios from 'axios';
import deleteMovie from './DeleteMovies';

class MovieList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movieData: [],
      res: [],
      show: false,
      selectedMovie: null,
    };
  }

  handleShow = (movieName) => {
    this.setState({ show: true, selectedMovie: movieName });
    this.fetchMovieDetails(movieName);
  };

  handleClose = () => this.setState({ show: false, selectedMovie: null });

  handleDeleteClick = (movieName) => {
    console.log(`Deleting movie: ${movieName}`);
    // Perform the delete operation and update the state
  };

  async fetchMovieDetails(movieName) {
    try {
      const apiKey = '5aefe5fe';
      const baseUrl = 'http://www.omdbapi.com/';

      const response = await axios.get(
        `${baseUrl}?apikey=${apiKey}&t=${encodeURIComponent(movieName)}&type=movie`
      );

      this.setState({ res: response.data });
      console.log(response.data);
    } catch (error) {
      console.error(`Error fetching details for ${movieName}`);
    }
  }

  componentDidMount() {
    this.fetchMovieData();
  }

  async fetchMovieData() {
    const { movies } = this.props;

    const moviePosters = await Promise.all(
      movies.map(async (movie) => {
        try {
          const response = await axios.get(
            `http://www.omdbapi.com/?apikey=5aefe5fe&t=${encodeURIComponent(movie)}&type=movie`
          );

          const responseAll = await axios.post('http://localhost:5000/search', {
            movieName: movie,
          });

          this.setState({ res: responseAll });
          console.log(responseAll.data);

          const data = response.data;

          if (data && data.Title && data.Type === 'movie') {
            return {
              name: movie,
              poster: data.Poster,
            };
          } else {
            return {
              name: movie,
              poster: null,
            };
          }
        } catch (error) {
          console.error(`Error fetching data for ${movie}`);
          return null;
        }
      })
    );

    const movieResults = moviePosters.filter((movie) => movie !== null);
    this.setState({ movieData: movieResults });
  }

  render() {
    const { movieData, show, res } = this.state;

    return (
      <div className="container mt-5">
        <div className="row">
          {movieData
            .sort((a, b) => a.name.localeCompare(b.name))
            .map((movie, index) => (
              <div key={index} className="col-md-4 mb-4" onClick={() => this.handleShow(movie.name)}>
                <div className="card">
                  {movie.poster ? (
                    <img
                      src={movie.poster}
                      className="card-img-top"
                      alt={`${movie.name} Poster`}
                      style={{ height: '500px' }}
                    />
                  ) : (
                    <div className="no-poster">
                      <p>No Image Found</p>
                    </div>
                  )}
                  <div className="card-body">
                    <h5 className="card-title">{movie.name}</h5>
                  </div>
                </div>
              </div>
            ))}
        </div>

        <Modal show={show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>{res.Title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>{res.Plot}</p>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={() => this.handleDeleteClick(res.Title)}>
              Delete
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
}

export default MovieList;
