// MovieUpdateForm.js
import React, { useState } from 'react';
import axios from 'axios';

const MovieUpdateForm = () => {
  const [updateData, setUpdateData] = useState({
    movieName: '',
    language: '',
    director: '',
    relaseYear: '',
    rating: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdateData({ ...updateData, [name]: value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    try {
      // Validate required fields
      if (!updateData.movieName) {
        console.error('Name is required.');
        return;
      }

      const updatedMovieData = {
        ...updateData,
        relaseYear: parseInt(updateData.relaseYear),
        rating: parseInt(updateData.rating),
      };

      const response = await axios.put(`http://localhost:5000/movies/${updateData.movieName}`, updatedMovieData);
      console.log(response.data);
    } catch (error) {
      console.error('Error updating movie:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center title">Update Movie</h2>
      <form onSubmit={handleFormSubmit}>
        <div className="mb-3">
          <label htmlFor="movieName" className="form-label label_color">
            Name:
          </label>
          <input
            type="text"
            className="form-control custom-input"
            id="movieName"
            name="movieName"
            value={updateData.movieName}
            onChange={handleInputChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="language" className="form-label label_color">
            Language:
          </label>
          <input
            type="text"
            className="form-control custom-input"
            id="language"
            name="language"
            value={updateData.language}
            onChange={handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="director" className="form-label label_color">
            Director:
          </label>
          <input
            type="text"
            className="form-control custom-input"
            id="director"
            name="director"
            value={updateData.director}
            onChange={handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="relaseYear" className="form-label label_color">
            Release Year:
          </label>
          <input
            type="number" 
            className="form-control custom-input"
            id="relaseYear"
            name="relaseYear"
            value={updateData.relaseYear}
            onChange={handleInputChange}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="rating" className="form-label label_color">
            Rating:
          </label>
          <input
            type="number"
            className="form-control custom-input"
            id="rating"
            name="rating"
            value={updateData.rating}
            onChange={handleInputChange}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Update
        </button>
      </form>
    </div>
  );
};

export default MovieUpdateForm;
