// MovieForm.js
import React, { useState } from 'react';
import axios from 'axios';

const MovieForm = ({ onClose, onMovieAdded }) => {
  const [movieData, setMovieData] = useState({
    movieName: '',
    language: '',
    director: '',
    releaseYear: '',
    rating: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMovieData({ ...movieData, [name]: value });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    try {
      // Convert the rating and releaseYear to integers
      const updatedMovieData = {
        ...movieData,
        releaseYear: parseInt(movieData.releaseYear),
        rating: parseInt(movieData.rating),
      };

      // Send a POST request to the backend with the movie data
      const response = await axios.put('http://localhost:5000/addMovies', updatedMovieData);
      console.log(response.data); // Log the response from the backend

      // Notify the parent component that a movie has been added
      onMovieAdded();

      // Close the form
      onClose();
    } catch (error) {
      console.error('Error adding movie:', error);
    }
  };

  return (
    <div>
      <h2>Add New Movie</h2>
      <form onSubmit={handleFormSubmit}>
        <label>
          Name:
          <input type="text" name="movieName" value={movieData.movieName} onChange={handleInputChange} required />
        </label>
        <br />
        <label>
          Language:
          <input type="text" name="language" value={movieData.language} onChange={handleInputChange} required />
        </label>
        <br />
        <label>
          Director:
          <input type="text" name="director" value={movieData.director} onChange={handleInputChange} required />
        </label>
        <br />
        <label>
          Release Year:
          <input type="number" name="releaseYear" value={movieData.releaseYear} onChange={handleInputChange} required />
        </label>
        <br />
        <label>
          Rating:
          <input type="number" name="rating" value={movieData.rating} onChange={handleInputChange} required />
        </label>
        <br />
        <button type="submit">Add Movie</button>
      </form>
    </div>
  );
};

export default MovieForm;
